# generated by stubbs:add-option
# Sun Sep 25 11:26:56 PDT 2011

# print USAGE and exit
rerun_option_error() {
    if [[ "$RERUN_COLOR" == "true" ]]
    then echo -e ${red}"SYNTAX: $*"${_red} >&2 
    else echo "SYNTAX: $*" >&2
    fi
    exit 2
}

# check option has its argument
rerun_option_check() {
    [ "$1" -lt 2 ] && rerun_option_error
}

# options: [name]
while [ "$#" -gt 0 ]; do
    OPT="$1"
    case "$OPT" in
         -m|--module) rerun_option_check $# ; MODULE=$2 ; shift ;;
        # unknown option
        -?)
            rerun_option_error
            ;;
        # end of options, just arguments left
        *)
          break
    esac
    shift
done

# If defaultable options variables are unset, set them to their DEFAULT


# Post processes the options
[ -z "$MODULE" ] && {
    echo "Module name: "
    read MODULE
}
